import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router';

function Chat() {
	let [accessToken, setToken] = useState();
	const { state } = useLocation();
	const { user } = state;
	useEffect(() => {
		setToken(user.accessToken);
	}, []);
	return <div>{user.accessToken}</div>;
}

export default Chat;
