import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Friend from './FriendCard';
import SvgIcon from '@mui/material/SvgIcon';
import { TextField } from '@mui/material';
import { Navigate, useLocation, useNavigate } from 'react-router';
import { Link } from 'react-router-dom';

function Chat() {
	let [token, setToken] = useState();
	let [isAuthenticated, setAuth] = useState();
	let [currentFriend, setCurr] = useState();
	let [friends, setFriends] = useState([]);
	const { state } = useLocation();
	const { userData } = state;
	const { accessToken, user } = userData;

	const list = () => {
		let list = friends.map((friend) => (
			<Friend key={friend._id} user={friend} setFriend={setCurr} />
		));
		return list;
	};

	const navigate = useNavigate();

	useEffect(() => {
		setToken(accessToken);
		axios
			.get(`/user?username=${user.username}`, {
				headers: {
					authorization: 'Bearer ' + accessToken,
				},
			})
			.then((res) => {
				if (res.status == 401) {
					setAuth(false);
					navigate('/error');
				} else {
					console.log(res);
					setFriends(res.data.user[0].friends);
					setAuth(true);
				}
			})
			.catch((err) => {
				let res = err.response.data;
				console.log(err);
			});
	}, []);

	return !isAuthenticated ? (
		<Navigate to={'/home/login'} />
	) : (
		<div className='chat-screen-wrapper'>
			<div className='friends-list'>
				<div className='info-bar'>
					<Link to='/login' className='logo' />
				</div>
				<div className='search-wrapper'>
					<TextField variant='outlined' label='Search' />
				</div>
				<div className='list'>{list()}</div>
			</div>
			<div className='chat-box'>
				<div className='info-bar'></div>
				{!currentFriend ? <div>Not In conversation</div> : <div>Chat</div>}
				<div className='mess-wrapper'>
					<TextField variant='outlined' label='Message' fullWidth />
				</div>
			</div>
		</div>
	);
}

export default Chat;
