import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import { Button } from '@mui/material';
import { emailValidator } from '../utils/validator';

function SignIn() {
	let [userName, setUserName] = useState();
	let [name, setName] = useState();
	let [email, setEmail] = useState();
	let [password, setPassword] = useState();
	let [confirmPassword, setConfirm] = useState();
	let [passwordError, setPassError] = useState(false);
	let [emaiError, setEmailError] = useState(false);

	const handleSubmit = (event) => {
		let err = false;
		if (confirmPassword != password) {
			setPassError(true);
			err = true;
		}
		if (email && !email.toLowerCase().match(emailValidator)) {
			setEmailError(true);
			err = true;
		}
		if (err) return;
		fetch('/auth', {
			method: 'POST',
			body: {
				userName: userName,
				name: name,
				email: email,
				password: password,
			},
		});
		return;
	};
	return (
		<div>
			<TextField
				label='Name'
				variant='standard'
				onChange={(event) => {
					setName(event.target.value);
				}}
			/>
			<br />

			<TextField
				label='Username'
				variant='standard'
				onChange={(event) => {
					setUserName(event.target.value);
				}}
			/>
			<br />
			{emaiError ? (
				<TextField
					error
					label='Email'
					variant='standard'
					helperText='Incorrect email format.'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			) : (
				<TextField
					label='Email'
					variant='standard'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			)}
			<br />
			<TextField
				label='Password'
				variant='standard'
				onChange={(event) => {
					setPassword(event.target.value);
				}}
			/>
			<br />
			{!passwordError ? (
				<TextField
					label='Confirm Password'
					variant='standard'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			) : (
				<TextField
					error
					label='Confirm Password'
					variant='standard'
					helperText='Passwords does not match'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			)}

			<br />
			<Button variant='outlined' onClick={handleSubmit}>
				Primary
			</Button>
		</div>
	);
}

export default SignIn;
