import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import { Button } from '@mui/material';
import { emailValidator } from '../utils/validator';
import { useNavigate } from 'react-router';

function SignIn() {
	let [username, setUserName] = useState();
	let [name, setName] = useState();
	let [email, setEmail] = useState();
	let [password, setPassword] = useState();
	let [confirmPassword, setConfirm] = useState();
	let [passwordError, setPassError] = useState(false);
	let [emaiError, setEmailError] = useState(false);

	const handleSubmit = (event) => {
		let err = false;
		if (confirmPassword != password) {
			setPassError(true);
			err = true;
		}
		if (email && !email.match(emailValidator)) {
			setEmailError(true);
			err = true;
		}
		if (err) return;
		const navigate = useNavigate;
		const newUser = {
			username: username,
			name: name,
			email: email,
			password: password,
		};
		fetch('/auth', {
			method: 'POST',
			body: JSON.stringify(newUser),
		})
			.then((response) => {
				if (response.status >= 200 && response.status < 300) {
					response.json().then((data) => console.log(data));
				}
			})
			.catch((err) => console.log(err));
		return;
	};
	return (
		<div>
			<TextField
				label='Name'
				variant='standard'
				onChange={(event) => {
					setName(event.target.value);
				}}
			/>
			<br />

			<TextField
				label='Username'
				variant='standard'
				onChange={(event) => {
					setUserName(event.target.value);
				}}
			/>
			<br />
			{emaiError ? (
				<TextField
					error
					label='Email'
					variant='standard'
					helperText='Incorrect email format.'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			) : (
				<TextField
					label='Email'
					variant='standard'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			)}
			<br />
			<TextField
				label='Password'
				variant='standard'
				onChange={(event) => {
					setPassword(event.target.value);
				}}
			/>
			<br />
			{!passwordError ? (
				<TextField
					label='Confirm Password'
					variant='standard'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			) : (
				<TextField
					error
					label='Confirm Password'
					variant='standard'
					helperText='Passwords does not match'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			)}

			<br />
			<Button variant='outlined' onClick={handleSubmit}>
				Primary
			</Button>
		</div>
	);
}

export default SignIn;
