import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import { Button } from '@mui/material';
import { emailValidator } from '../utils/validator';
import { useNavigate } from 'react-router';

function SignIn() {
	let [username, setUserName] = useState();
	let [name, setName] = useState();
	let [email, setEmail] = useState();
	let [password, setPassword] = useState();
	let [confirmPassword, setConfirm] = useState();
	let [passwordError, setPassError] = useState(false);
	let [emailError, setEmailError] = useState(false);

	const navigate = useNavigate();

	const handleSubmit = (event) => {
		let err = false;
		if (confirmPassword !== password) {
			setPassError(true);
			err = true;
		}
		if (email && !email.match(emailValidator)) {
			setEmailError(true);
			err = true;
		}
		if (err) return;
		const newUser = {
			username: username,
			name: name,
			email: email,
			password: password,
		};
		console.log(newUser);
		fetch('/auth/signup', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(newUser),
		})
			.then((response) => {
				console.log(response.status);
				if (response.status >= 200 && response.status < 300) {
					response.json().then((data) => navigate('/user', { user: data }));
				}
			})
			.catch((err) => console.log(err));
	};
	return (
		<div>
			<TextField
				label='Name'
				variant='standard'
				onChange={(event) => {
					setName(event.target.value);
				}}
			/>
			<TextField
				label='Username'
				variant='standard'
				onChange={(event) => {
					setUserName(event.target.value);
				}}
			/>
			{emailError ? (
				<TextField
					error
					label='Email'
					variant='standard'
					helperText='Incorrect email format.'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			) : (
				<TextField
					label='Email'
					variant='standard'
					onChange={(event) => {
						setEmail(event.target.value);
					}}
				/>
			)}
			<TextField
				label='Password'
				variant='standard'
				onChange={(event) => {
					setPassword(event.target.value);
				}}
			/>
			{!passwordError ? (
				<TextField
					label='Confirm Password'
					variant='standard'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			) : (
				<TextField
					error
					label='Confirm Password'
					variant='standard'
					helperText='Passwords does not match'
					onChange={(event) => {
						setConfirm(event.target.value);
					}}
				/>
			)}

			<br />
			<Button variant='outlined' onClick={handleSubmit}>
				Let's Go
			</Button>
		</div>
	);
}

export default SignIn;
