import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router';

function ChatBox({ user, friend, token }) {
	let [messages, setMessages] = useState([]);
	const navigate = useNavigate();

	const list = () => {
		let list = messages.map((mess) => <Message key={mess._id} mess={mess} />);
		return list;
	};
	useEffect(() => {
		axios
			.get(`/mess?user_id=${user._id}&friend_id=${friend._id}`, {
				headers: {
					authorization: 'Bearer ' + token,
				},
			})
			.then((res) => {
				console.log(res);
				setMessages(res.data.user);
			})
			.catch((err) => {
				navigate('/err');
			});
	}, []);

	return <div className='chat-box-wrapper'>{list()}</div>;
}

function Message({ mess }) {
	return (
		<>
			<div className='message'>{mess.message}</div>
			<div className='mess-wrapper'>
				<InputBase
					sx={{ ml: 1, flex: 1 }}
					placeholder='Search Google Maps'
					inputProps={{ 'aria-label': 'Input message' }}
					fullWidth
				/>
				<SendIcon />
			</div>
		</>
	);
}

export default ChatBox;
